import kotlin.collections.ArrayList
import java.util.Base64


class IndividualSearchResultAdapter(var context: Context, var listener:(String, String) -> Unit) : RecyclerView.Adapter<IndividualSearchResultAdapter.ViewHolder>(){


    var searchList : MutableList<IndividualSearchResultResponseModel.Data> = ArrayList()
    class ViewHolder(itemView: SearchItemsBinding) : RecyclerView.ViewHolder(itemView.root) {
        val binding = itemView
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val binding = SearchItemsBinding.inflate(LayoutInflater.from(context), parent, false)
        return ViewHolder(binding)
    }

    @RequiresApi(Build.VERSION_CODES.O)
    override fun onBindViewHolder(holder: ViewHolder, position: Int) {


        var itemList=searchList[position]

        holder.binding.username.text=searchList[position].username.toString()


        //var imageBytes ="".toByteArray()
       // var imageBytes = searchList[position].userimage?.toByteArray()
            //        val imageByteArray: ByteArray = Base64.getDecoder().decode(imageBytes)
//        val decodedString = String(imageByteArray)

//        val bmp = BitmapFactory.decodeByteArray(imageBytes, 0, imageBytes!!.size)
//        holder.binding.userProfile.setImageBitmap(bmp)

      //  Log.d("TAG", "onBindViewHolder: "+decodedString)\


        Log.d("TAG", "onBindViewHolder: "+position)

        if ( searchList[position].userimage!=null) {
            Glide.with(context)
                .load(Constants.BaseUrl+searchList[position].userimage)
                .diskCacheStrategy(DiskCacheStrategy.ALL)
                .placeholder(R.drawable.logo)
                .into(holder.binding.userProfile)
        }else{
            Glide.with(context)
                .load(R.drawable.ic_user)
                .diskCacheStrategy(DiskCacheStrategy.ALL)
                .placeholder(R.drawable.logo)
                .into(holder.binding.userProfile)
        }



        holder.itemView.setOnClickListener{

            Log.d("TAG", "onBindViewHolder: "+searchList[holder.adapterPosition].username.toString()+searchList[holder.adapterPosition].userimage.toString())
            listener.invoke(searchList[holder.adapterPosition].username.toString(),searchList[holder.adapterPosition].userimage.toString())
        }




//        Glide.with(context)
//            .load("http://via.placeholder.com/300.png")
//            .into(holder.binding.userProfile)
    }

    override fun getItemCount(): Int {
        return searchList.size
    }

    // add data when data is still present
    fun addData(searchItem:List<IndividualSearchResultResponseModel.Data>) {
        searchList.addAll(searchItem)
        notifyItemInserted(searchList.size - searchItem.size)
    }

    // add data when data is for first time
    fun addDataForFirstTime(searchItem:List<IndividualSearchResultResponseModel.Data>) {
        searchList.clear()
        searchList.addAll(searchItem)
        notifyDataSetChanged()
    }

//    fun addData(searchItem:List<SearchResponseModel.Data> ){
//        searchList.addAll(searchItem)
//        notifyItemInserted(searchList.size - searchItem.size)
////        searchList.clear()
////        searchList.addAll(searchItem)
////        notifyDataSetChanged()
//      //  notifyItemInserted(searchList.size - searchItem.size)
//    }
}